import { React, useState } from "react";
import { AppBar, Toolbar, Button, IconButton } from "@material-ui/core";
import { MenuIcon, Menu, MenuItem } from "@material-ui/icons/Menu";

const NavBar = ({ logo, title, btn }) => {
  const [anchorEl, setAnchorEl] = useState(null);

  const handleMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClickMenu = () => {
    setAnchorEl(null);
  };
  return (
    <AppBar position="fixed">
      <Toolbar>
        <IconButton>
          <MenuIcon />
        </IconButton>
        <Menu
          id="menu-appbar"
          anchorEl={anchorEl}
          anchorOrigin={{
            vertical: "top",
            horizontal: "right",
          }}
          keepMounted
          transformOrigin={{
            vertical: "top",
            horizontal: "right",
          }}
          open={open}
          onClose={handleClickMenu}
        >
          <MenuItem onClick={handleClickMenu}>Profile</MenuItem>
          <MenuItem onClick={handleClickMenu}>My account</MenuItem>
        </Menu>
        <IconButton>{logo}</IconButton>
        <Button>{btn}</Button>
      </Toolbar>
    </AppBar>
  );
};

export default NavBar;
