import { Grid, Button, Container, Typography } from "@material-ui/core";
import { Form, Formik, useFormikContext } from "formik";
import React, { useState } from "react";
import Textfield from "../FormsUI/Textfield";
import PasswordInput from "./PasswordInput";
import validationSchema from "./validationSchema.js";
import initialValues from "./initialValues.js";
import { GoogleLogin } from "react-google-login";

const LogIn = ({}) => {
  const [isSignUp, setSignUp] = useState(false);

  const handleSubmit = (values) => {
    console.log(values);
  };
  const toggleSignUp = () => {
    setSignUp((prevState) => !prevState);
  };
  const responseGoogle = async (response) => {
    console.log(response);
  };
  return (
    <Container maxWidth="xs">
      <Formik
        initialValues={initialValues}
        validationSchema={validationSchema}
        onSubmit={handleSubmit}
      >
        <Form>
          <Grid container spacing={2}>
            {isSignUp && (
              <>
                <Grid item xs={12} md={6}>
                  <Textfield name="firstName" label="First name" />
                </Grid>
                <Grid item xs={12} md={6}>
                  <Textfield name="lastName" label="Last Name" />
                </Grid>
              </>
            )}
            <Grid item xs={12}>
              <Textfield name="email" label="E-Mail" />
            </Grid>
            <PasswordInput name="password" confirmation={isSignUp} />
            <Grid item xs={12}>
              <Button
                variant="contained"
                fullWidth
                color="primary"
                // onClick={handleSubmit}
                type="submit"
              >
                Sign In
              </Button>
            </Grid>
            <Grid item xs={12}>
              <GoogleLogin
                clientId="109263540642-m011hv8epkc5d5q3kqhspe619c02graa.apps.googleusercontent.com"
                render={(renderProps) => (
                  <Button
                    onClick={renderProps.onClick}
                    disabled={renderProps.disabled}
                    fullWidth
                    variant="contained"
                    color="primary"
                  >
                    Google Sign In
                  </Button>
                )}
                onSuccess={responseGoogle}
                onFailure={responseGoogle}
                cookiePolicy={"single_host_origin"}
              />
            </Grid>
            <Grid item xs={12}>
              <Button onClick={toggleSignUp} fullWidth>
                <Typography variant="subtitle2">
                  {isSignUp
                    ? "Already have an account? Log in"
                    : "Don't have an account? Sign up"}
                </Typography>
              </Button>
            </Grid>
          </Grid>
        </Form>
      </Formik>
    </Container>
  );
};

export default LogIn;
