import React, { useContext } from "react";
import { Link } from "react-router-dom";
import AppContext from "../context/AppContext";
import { sumTotal } from "../utils/sumTotal";
import "../styles/components/Checkout.css";
import {
  IconButton,
  Button,
  Container,
  Grid,
  Typography,
  List,
  ListItem,
  Divider,
} from "@material-ui/core";
import Delete from "@material-ui/icons/Delete";

const Checkout = () => {
  const { state, removeFromCart } = useContext(AppContext);
  const { cart } = state;

  const handleRemove = (product) => {
    removeFromCart(product);
  };

  // const handleSumTotal = () => {
  //   const reducer = (accumulator, currentValue) =>
  //     accumulator + currentValue.price;
  //   const sum = cart.reduce(reducer, 0);
  //   return sum;
  // };

  const handleSumTotal = () => {
    sumTotal(cart);
  };

  return (
    <Container maxWidth="lg">
      <Grid container justify="space-between">
        <Grid item>
          {cart.length > 0 ? (
            <Typography variant="h4">Lista de Pedidos:</Typography>
          ) : (
            <>
              <Typography variant="h4">Your cart is empty</Typography>
              <Button variant="contained" color="primary">
                <Link to="/">Continue Shopping!</Link>
              </Button>
            </>
          )}
          {cart.map((product) => (
            <List>
              <ListItem key={product.id} divider={true}>
                <h4>{product.title}</h4>
                <span>${product.price}</span>
                <IconButton onClick={() => handleRemove(product)}>
                  {/* <i className="fas fa-trash-alt" /> */}
                  <Delete />
                </IconButton>
              </ListItem>
            </List>
          ))}
        </Grid>
        <Grid item>
          <Typography variant="h5">{`Precio total: $ ${sumTotal(
            cart
          )}`}</Typography>
          <Button variant="contained" color="primary">
            <Link to="/checkout/information">Proceed to Checkout</Link>
          </Button>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Checkout;
