import React from "react";
import {
  Dialog,
  Zoom,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  Button,
  makeStyles,
} from "@material-ui/core";

const Modal = ({ product, button, isOpen, onClose, classes }) => {
  return (
    <Dialog
      open={isOpen}
      // TransitionComponent={<Slide />}
      // keepMounted
      onClose={onClose}
      aria-labelledby="alert-dialog-slide-title"
      aria-describedby="alert-dialog-slide-description"
      className={classes.modal}
    >
      <DialogTitle id="alert-dialog-slide-title">{product.title}</DialogTitle>
      <DialogContent>
        <figure className={classes.modal__fig}>
          <img
            alt={product.title}
            src={product.image}
            className={classes.modal__img}
          />
        </figure>
        <DialogContentText id="alert-dialog-slide-description">
          Let Google help apps determine location. This means sending anonymous
          location data to Google, even when no apps are running.
        </DialogContentText>
      </DialogContent>
      <DialogActions>{button}</DialogActions>
    </Dialog>
  );
};

export default Modal;
