import {
  Button,
  Card,
  CardActions,
  CardContent,
  CardMedia,
  CardActionArea,
  Typography,
  makeStyles,
  CardHeader,
} from "@material-ui/core";
import React from "react";

const useStyles = makeStyles((theme) => ({
  root: {
    // maxWidth: 345,
    // minHeight: "300px",
  },
  Product__img: {
    paddingTop: "56.25%",
    maxHeight: 200,
    width: "auto",
    // margin: "0 auto",
  },
}));

const Product = ({ product, handleAddToCart }) => {
  const classes = useStyles();
  return (
    <Card className={classes.root} raised>
      <CardActionArea>
        <CardHeader
          title={
            <Typography variant="h4" align="center">
              {product.title}
            </Typography>
          }
        />
        <CardMedia
          className={classes.Product__img}
          image={product.image}
          title={product.title}
        />
        <CardContent>
          <Typography variant="subtitle2">${product.price}</Typography>
          <Typography variant="caption">{product.description}</Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
        <Button
          variant="contained"
          color="primary"
          fullWidth
          onClick={() => handleAddToCart(product)}
        >
          Add To Cart
        </Button>
      </CardActions>
    </Card>
  );
};

export default Product;
